mapper.select:
select rnum ,SEQ ,MEM_NAME ,MEM_ID ,BOARD_SUBJECT ,BOARD_CONTENT ,REG_DATE ,UPT_DATE ,VIEW_CNT from ( select row_number() over(order by seq desc) rnum ,SEQ ,MEM_NAME ,MEM_ID ,BOARD_SUBJECT ,BOARD_CONTENT ,REG_DATE ,UPT_DATE ,VIEW_CNT from board_study where 1=1
and mem_name like '%' || #{searchTxt} || '%'
and BOARD_SUBJECT like '%' || #{searchTxt} || '%'
and (BOARD_SUBJECT like '%' || #{searchTxt} || '%' or BOARD_CONTENT like '%' || #{searchTxt} || '%')
and to_char(reg_date, 'yyyy-MM-dd') between #{cal1} and #{cal2}
) where rnum between ${pageBegin} and ${pageEnd}

mapper.update:
UPDATE 
	BOARD_STUDY
SET
	MEM_NAME = #{writer}
    ,MEM_ID = #{id}
    ,BOARD_SUBJECT = #{title}
    ,BOARD_CONTENT = #{text}
    ,UPT_DATE = SYSDATE
WHERE SEQ = #{seq}

mapper.delete:
DELETE FROM BOARD_STUDY
WHERE SEQ IN

	#{checkedList}

mapper.insert:
	insert into file_study_table (
                FILE_SEQ
                ,REAL_NAME
                ,SAVE_NAME
                ,REG_DATE
                ,SAVE_PATH
                ,LIST_SEQ
			) values (
                (SELECT NVL(max(FILE_SEQ), 0) + 1 FROM file_study_table)
				,#{realName}
				,#{saveName}
				,SYSDATE
                ,#{savePath}
				,#{seq}
			)
